import React, { useEffect } from 'react'
import { StyleSheet, Text, View, Image, TextInput, ScrollView, Keyboard, Alert,Button } from 'react-native';
import axios from 'axios'
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Box, Heading, VStack, FormControl, Input, Center, NativeBaseProvider,  } from "native-base";

export default function Login({ navigation }) {
    const [inputs, setInputs] = React.useState({ email: '', password: '' });
    const [errors, setErrors] = React.useState({});
    const [loading, setLoading] = React.useState(false);
    const [failedLogin, setfailedLogin] = React.useState(false);

    const validate = async () => {
        console.log(errors)
        Keyboard.dismiss();
        let isValid = true;
        if (!inputs.email) {
            handleError('Please write an email', 'email');
            isValid = false;
        }
        if (!inputs.password) {
            handleError('Please write password', 'password');
            isValid = false;
        }
        if (isValid) {

            login(inputs);
        }
    };

    /*     // Login user
        const login = async (inputs) => {
            try {
                const response = await axios.post("http://192.168.1.14:5000/users/" + 'login', inputs)
                const jsonValue = JSON.stringify(response.data)
                await AsyncStorage.setItem('user', jsonValue)
                navigation.navigate('AffectedZonesList')
            } catch (e) {
                setfailedLogin(true)
    
                console.log("save to storage failed")
            }
    
        } */

    /*    const getDataFromStorage = async () => {
           try {
               const jsonValue = await AsyncStorage.getItem('user')
               return jsonValue != null ? JSON.parse(jsonValue) : null;
           } catch (e) {
               console.log("error getDataFromStorage")
           }
       }
    */


    useEffect(() => {
        /*   getDataFromStorage().then((val) => {
              if (val) {
                  navigation.navigate('AffectedZonesList')
              }
          }); */
    }, [])


    const handleOnchange = (text, input) => {
        setInputs(prevState => ({ ...prevState, [input]: text }));
    };

    const handleError = (error, input) => {
        setErrors(prevState => ({ ...prevState, [input]: error }));
    };

    return (
        <View style={styles.container} >

            <Center w="100%">
                <Box safeArea p="2" w="90%" maxW="290" py="8" h="100%">
                    <Heading size="lg" color="coolGray.800" _dark={{
                        color: "warmGray.50"
                    }} fontWeight="semibold">
                        Welcome
                    </Heading>
                    <Heading mt="1" color="coolGray.600" _dark={{
                        color: "warmGray.200"
                    }} fontWeight="medium" size="xs">
                        Sign up to continue!
                    </Heading>
                    <VStack space={3} mt="5">
                        <FormControl>
                            <FormControl.Label>FirstName</FormControl.Label>
                            <Input />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>LastName</FormControl.Label>
                            <Input />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>Phone</FormControl.Label>
                            <Input />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>Adress</FormControl.Label>
                            <Input />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>Email</FormControl.Label>
                            <Input />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>Password</FormControl.Label>
                            <Input type="password" />
                        </FormControl>
                        <FormControl>
                            <FormControl.Label>Confirm Password</FormControl.Label>
                            <Input type="password" />
                        </FormControl>
                      

                        <Button mt="2"
                            title="Log in"
                            color="#2196F3"
                            accessibilityLabel="Log in"
                        />
                        
                         
                    </VStack>
                </Box>
            </Center>;


        </View>

    )



}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        /*  alignItems: 'center',
         justifyContent: 'center', */
    },
    container2: {

        alignItems: 'center',
        justifyContent: 'center',
    },
    tinyLogo: {
        width: 100,
        height: 100,
    },
    input: {
        height: 40,
        margin: 12,
        // borderWidth: 1,
    },
    firstTitle: {
        fontWeight: 'bold',
        fontSize: 30,

    },
    secondTitle: {
        fontWeight: 'bold',
        color: '#c1c3c2',

    },

});
