import React, { useEffect } from 'react'
import { StyleSheet, Text, View, Image, TextInput, ScrollView, Keyboard, Alert ,} from 'react-native';
import axios from 'axios'
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Box, Heading, FormControl, Input, Center, NativeBaseProvider,Link,VStack,HStack,Button } from "native-base";

export default function Login({ navigation }) {
    const [inputs, setInputs] = React.useState({ email: '', password: '' });
    const [errors, setErrors] = React.useState({});
    const [loading, setLoading] = React.useState(false);
    const [failedLogin, setfailedLogin] = React.useState(false);

    const validate = async () => {
        console.log(errors)
        Keyboard.dismiss();
        let isValid = true;
        if (!inputs.email) {
            handleError('Please write an email', 'email');
            isValid = false;
        }
        if (!inputs.password) {
            handleError('Please write password', 'password');
            isValid = false;
        }
        if (isValid) {

            login(inputs);
        }
    };

    /*     // Login user
        const login = async (inputs) => {
            try {
                const response = await axios.post("http://192.168.1.14:5000/users/" + 'login', inputs)
                const jsonValue = JSON.stringify(response.data)
                await AsyncStorage.setItem('user', jsonValue)
                navigation.navigate('AffectedZonesList')
            } catch (e) {
                setfailedLogin(true)
    
                console.log("save to storage failed")
            }
    
        } */

    /*    const getDataFromStorage = async () => {
           try {
               const jsonValue = await AsyncStorage.getItem('user')
               return jsonValue != null ? JSON.parse(jsonValue) : null;
           } catch (e) {
               console.log("error getDataFromStorage")
           }
       }
    */


    useEffect(() => {
        /*   getDataFromStorage().then((val) => {
              if (val) {
                  navigation.navigate('AffectedZonesList')
              }
          }); */
    }, [])


    const handleOnchange = (text, input) => {
        setInputs(prevState => ({ ...prevState, [input]: text }));
    };

    const handleError = (error, input) => {
        setErrors(prevState => ({ ...prevState, [input]: error }));
    };

    return (
        <View >

<Center w="100%">
      <Box safeArea p="2" py="8" w="90%" maxW="290">
        <Heading size="lg" fontWeight="600" color="coolGray.800" _dark={{
        color: "warmGray.50"
      }}>
          Welcome
        </Heading>
        <Heading mt="1" _dark={{
        color: "warmGray.200"
      }} color="coolGray.600" fontWeight="medium" size="xs">
          Sign in to continue!
        </Heading>

        <VStack space={3} mt="5">
          <FormControl>
            <FormControl.Label>Email ID</FormControl.Label>
            <Input />
          </FormControl>
          <FormControl>
            <FormControl.Label>Password</FormControl.Label>
            <Input type="password" />
            <Link _text={{
            fontSize: "xs",
            fontWeight: "500",
            color: "indigo.500"
          }} alignSelf="flex-end" mt="1">
              Forget Password?
            </Link>
          </FormControl>
          <Button mt="2" colorScheme="indigo">
            Sign in
          </Button>
          <HStack mt="6" justifyContent="center">
            <Text fontSize="sm" color="coolGray.600" _dark={{
            color: "warmGray.200"
          }}>
              I'm a new user.{" "}
            </Text>
            <Link _text={{
            color: "indigo.500",
            fontWeight: "medium",
            fontSize: "sm"
          }} href="#">
              Sign Up
            </Link>
          </HStack>
        </VStack>
      </Box>
    </Center>;


        </View>

    )



}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        /*     alignItems: 'center',
            justifyContent: 'center', */
    },
    container2: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    tinyLogo: {
        width: 100,
        height: 100,
    },
    input: {
        height: 40,
        margin: 12,
        // borderWidth: 1,
    },
    firstTitle: {
        fontWeight: 'bold',
        fontSize: 30,

    },
    secondTitle: {
        fontWeight: 'bold',
        color: '#c1c3c2',

    },

});
